import React, { useState } from "react";
import {
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  makeStyles
} from "@material-ui/core";


const useStyles = makeStyles({
  dialog: {
    color: "#1DA1F2",
  },
  submitButton: {
    margin: 10,
    width: 100,
    fontWeight: 800,
    color: "white",
    backgroundColor: "#1DA1F2",
    "&:hover": {
      backgroundColor: "lightblue",
      color: "#1DA1F2",
   }
  }
});

const AlertWindow = (props) => {
  const { submitButton, dialog } = useStyles();
  const { isOpen, onConfirm, title, msg } = props;

  return (
    <Dialog open={open} onClose={props.function} style={{display: isOpen || 'none'}}>
      <DialogTitle id="alert-dialog-title" className={dialog}>
        {title}
      </DialogTitle>
      <DialogContent>
        <DialogContentText id="alert-dialog-description">
          {msg}
        </DialogContentText>
      </DialogContent>
      <DialogActions>
        <Button
          color="default"
          variant="contained"
          className={submitButton}
          onClick={() => onConfirm(false)}
        >
          OK
        </Button>
      </DialogActions>
    </Dialog>
  );
};


export default AlertWindow;
